pragma cashscript ^0.10.0;

contract CashStarterStop() {
    function stop() {
        require(this.activeInputIndex == 0);
        require(tx.inputs[1].tokenCategory == 0x64c9ea104e07d9099bc3cdcb2a0035286773790c40dbcb0ae67068b1b8453748 + 0x02);
        require(tx.inputs.length == 2);
        require(tx.outputs.length <= 2);

        bytes campaignID = tx.inputs[1].nftCommitment.split(35)[1];
        require(campaignID != 0xFFFFFFFFFF);

        bytes masterID = tx.inputs[0].nftCommitment.split(35)[1];
        require(masterID == 0xFFFFFFFFFF);

        bytes4 endBlock = bytes4(tx.inputs[1].nftCommitment.split(26)[1].split(4)[0]);
        require(tx.time >= int(endBlock));

        require(tx.outputs[0].lockingBytecode == tx.inputs[0].lockingBytecode);
        require(tx.outputs[0].tokenCategory == tx.inputs[0].tokenCategory);
        require(tx.outputs[0].tokenAmount == tx.inputs[0].tokenAmount);
        require(tx.outputs[0].nftCommitment == tx.inputs[0].nftCommitment);
        require(tx.outputs[0].value == tx.inputs[0].value);

        if (tx.inputs[1].value == 1000) {
            require(tx.outputs.length == 1);

        } else {
            require(tx.outputs[1].lockingBytecode == tx.inputs[1].lockingBytecode);
            require(tx.outputs[1].tokenCategory == tx.inputs[1].tokenCategory.split(32)[0] + 0x01);
            require(tx.outputs[1].tokenAmount == tx.inputs[1].tokenAmount);
            require(tx.outputs[1].nftCommitment == tx.inputs[1].nftCommitment);
            require(tx.outputs[1].value == tx.inputs[1].value - 1000);
        }
    }
}
